import { PhongNode } from 'three/examples/jsm/nodes/materials/nodes/PhongNode.js';
import { NodeMaterial } from 'three/examples/jsm/nodes/materials/NodeMaterial.js';
import { NodeUtils } from 'three/examples/jsm/nodes/core/NodeUtils.js';

function PhongNodeMaterial() {

	var node = new PhongNode();

	NodeMaterial.call( this, node, node );

	this.type = 'PhongNodeMaterial';

}

PhongNodeMaterial.prototype = Object.create( NodeMaterial.prototype );
PhongNodeMaterial.prototype.constructor = PhongNodeMaterial;

NodeUtils.addShortcuts( PhongNodeMaterial.prototype, 'fragment', [
	'color',
	'alpha',
	'specular',
	'shininess',
	'normal',
	'emissive',
	'ambient',
	'light',
	'shadow',
	'ao',
	'environment',
	'environmentAlpha',
	'mask',
	'position'
] );

export { PhongNodeMaterial };
