import { StandardNode } from 'three/examples/jsm/nodes/materials/nodes/StandardNode.js';
import { NodeMaterial } from 'three/examples/jsm/nodes/materials/NodeMaterial.js';
import { NodeUtils } from 'three/examples/jsm/nodes/core/NodeUtils.js';

function StandardNodeMaterial() {

	var node = new StandardNode();

	NodeMaterial.call( this, node, node );

	this.type = 'StandardNodeMaterial';

}

StandardNodeMaterial.prototype = Object.create( NodeMaterial.prototype );
StandardNodeMaterial.prototype.constructor = StandardNodeMaterial;

NodeUtils.addShortcuts( StandardNodeMaterial.prototype, 'fragment', [
	'color',
	'alpha',
	'roughness',
	'metalness',
	'reflectivity',
	'clearcoat',
	'clearcoatRoughness',
	'clearcoatNormal',
	'normal',
	'emissive',
	'ambient',
	'light',
	'shadow',
	'ao',
	'environment',
	'mask',
	'position',
	'sheen'
] );

export { StandardNodeMaterial };
