import FloatNode from 'three/examples/jsm/renderers/nodes/inputs/FloatNode.js';
import { NodeUpdateType } from 'three/examples/jsm/renderers/nodes/core/constants.js';

class TimerNode extends FloatNode {

	constructor() {

		super();

		this.updateType = NodeUpdateType.Frame;

	}

	update( frame ) {

		this.value = frame.time;

	}

}

export default TimerNode;
